INSERT INTO `about_us_a` (`id`, `title`, `phone_number`, `fax_number`, `email`, `web`, `po_box`, `description`, `thumbnail_image`) VALUES
(1, '           مركز معلومات دبى لسباق الخيل ', '', '', '', '', '', '<p style="direction: rtl;"><span style="color:#008000"><strong>العاديات</strong> </span>مجلة دورية متخصصة في الخيل وسباقاتها وأخبارها وكافة أنشطة الفروسية الأخرى.</p>\r\n\r\n<p style="direction: rtl;">اختار لها هذا الاسم الرنان</p>\r\n\r\n<p style="direction: rtl;">صاحب السمو الشيخ <strong><span style="color:#FF0000">محمد بن راشد آل مكتوم</span></strong>، وهي تصدر عن مركز معلومات دبي لسباق الخيل بصفة اسبوعية وتغطي الموسم المحلي والعالمي لسباقات الخيل ومختلف مناشط الفروسية الأخرى بما فيها سباقات القدرة، قفز الحواجز، البولو، ومنافسات عروض جمال الخيل وغيرها. كما تعنى المجلة أيضا بتربية الخيول ومزاداتها ومختلف الشؤون البيطرية المتعلقه بها، بما فيها الأبحاث العلمية الحديثة، وهي مملوكة لحكومة دبي وتصدر بانتظام منذ الرابع من شهر نوفمبر عام 1994.</p>\r\n\r\n<div style="direction: rtl;">والعاديات التي تعتبر أول مجلة وطنية رسمية تصدر اسبوعيا باللغتين العربية والإنجليزية في دولة الإمارات، أصبحت الخيار المفضل لأهل الخيل ولها قراء ومشتركون من جميع أنحاء العالم. وقد استطاعت المجلة تجنيد كفاءات صحافية وإعلامية وفنية مشهود لها بالدقة والكفاءة. وتتبنى المجلة نهجا تحريريا متوازنا يقوم على الموضوعية والشفافية وتحري الدقة في النقل والتحليل والبعد عن التسطيح فضلا عن الإحاطة التخصصية بأخبار وشؤون الفروسية وسباقات الخيل محليا وعالميا وتزويد القراء بالحقائق والمعلومات والأخبار الصحيحة والموثوقة في هذا المجال.&nbsp;</div>\r\n\r\n<div style="direction: rtl;">وتستكتب العاديات كوكبة من ألمع الكتاب العالميين المختصين في الفروسية وسباقات الخيل من بريطانيا، إيرلندا، الولايات المتحدة، استراليا، جنوب أفريقيا وغيرها من الدول ذات الثقل الكبير في هذا المجال، ولها مراسلون في جميع دول مجلس التعاون الخليجي وبعض الدول العربية، بالإضافة الى كوادر محلية عالية التخصص تتولى تغطية ومتابعة جميع الأنشطة المتعلقة بالفروسية وسباقات الخيل محليا، اقليميا وعالميا. المجلة لها شبكة توزيع واسعة النطاق تغطي دولة الإمارات العربية المتحدة حيث يتم توزيعها في الدواوين الرسمية، أندية ومضامير واسطبلات الخيل والسباقات، الفنادق الكبرى والمؤسسات الحكومية بدبي، كما تتوفر بسعر رمزي في جميع محطات إيبكو في دبي والإمارات الشمالية.</div>\r\n\r\n<p style="direction: rtl; text-align: right;">&nbsp;</p>\r\n', 'aboutus_547483d8836c1.jpg');

