CREATE TABLE IF NOT EXISTS `general_news` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(150) NOT NULL,
  `review_text` varchar(30) NOT NULL,
  `thumbnail_image` varchar(100) NOT NULL,
  `news_details_description` text NOT NULL,
  `news_date` date NOT NULL,
  `data` text NOT NULL,
  `news_cata` text NOT NULL,
  `is_active` varchar(11) NOT NULL DEFAULT '1',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 COMMENT='any news related to horse races will come into this table...' AUTO_INCREMENT=242 ;

